        public override void SetSyncLockTarget(Ientity entity)
        {
            if (SyncLockTarget == entity)
            {
                return;
            }
            if (SyncLockTarget != null && SyncLockTarget.realObject != null)
            {
                if (SyncLockTarget.XueTiao != null)
                {
                    SyncLockTarget.XueTiao.IsXueTiaoCpVib(false);
                }
            }
            this.DeltLockTargetXuetiao(entity);
            this.isTargetChanged = true;
            this.SyncLockTarget = entity;

            OnFocusTargetChanged();
            UInt64 objId = (entity == null) ? 0 : entity.GameObjGUID;
            CGLCtrl_GameLogic.Instance.EmsgToss_AskLockTarget(objId);
            if (entity != null && entity is Iplayer)
            {
                AddOrDelEnemy((Iplayer)entity, true);
            }
            else
            {
                AddOrDelEnemy(null, true);
            }
            EventCenter.Broadcast<Ientity>(EGameEvent.eGameEvent_LockTarget, entity);
        }

